DELIMITER //CREATE PROCEDURE add_venta(num_one INT, num_two INT, num_tree INT)	BEGIN		DECLARE mayor int;		IF num_one > num_two AND num_one > num_tree THEN			SET mayor := num_one;			  IF num_two > num_tree THEN					SET mayor := mayor + num_two;			  	ELSE 					SET mayor := mayor + num_tree;			  END IF;		ELSE IF num_two > num_one AND num_two > num_tree THEN 			SET mayor := num_two;			 	IF num_one > num_tree THEN					SET mayor := mayor + num_one;				  ELSE					SET mayor := mayor + num_tree;				END IF;		ELSE IF num_tree > num_one AND num_tree > num_two THEN			SET mayor := num_tree;			 	IF num_one > num_two THEN					SET mayor := mayor + num_one;				  ELSE					SET mayor := mayor + num_two;				END IF;	  		END IF;		INSERT INTO VENTAS (total_ventas) VALUES (mayor);	END ;DELIMITER  ;